import React from "react";

const TodoForm=(props)=> {
  const {setTodo,todo,todoList,setTodoList} = props
  // Creating a local state to have currently writing
  // todo item that will be sent to the global store.

  const handleTodoChange=(e)=> {
    setTodo(e.target.value);
  }

  const handleTodoAdd=()=> {
    const key =new Date().getTime()
    setTodoList([...todoList,{key:2,text:'asdfasdf'}])
    setTodo("");
  }

  const handleSubmitForm=(event)=> {
    if (event.keyCode === 13) handleTodoAdd();
  }

  return (
    <div className="row">
      <div className="col-md-12">
        <br />
        <div className="input-group">
          <input
            className="form-control"
            value={todo}
            autoFocus={true}
            placeholder="Enter new todo"
            onKeyUp={handleSubmitForm}
            onChange={handleTodoChange}
          />
          <div className="input-group-append">
            <button className="btn btn-primary" onClick={handleTodoAdd}>
              Add
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
export default TodoForm
